import { DuetLocale } from "../common-types";
/**
 * Baseline interface for all form components, including inputs and buttons
 */
export interface FormComponent<TValue = string> {
  value: TValue;
  disabled: boolean;
  identifier: string;
  name: string;
  setFocus(options?: FocusOptions): Promise<void>;
  accessibleActiveDescendant: string;
  accessibleControls: string;
  accessibleOwns: string;
}
/**
 * Interface for form components that function as inputs
 */
export interface InputComponent<TValue = string> extends FormComponent<TValue> {
  required: boolean;
  label: string;
}
/**
 * Interface used by numeric inputs with step functionality
 */
export interface DuetNumericInput<TValue extends string | number = number> extends InputComponent<TValue> {
  step: number;
  min: number;
  max: number;
  unit: string;
  locale: DuetLocale;
}
